@Component({
  selector: 'app-success-alert-from-cli',
  templateUrl: './success-alert-from-cli.component.html',
  styleUrls: ['./success-alert-from-cli.component.css']
})
export class SuccessAlertFromCliComponent {
  successColor(){
    return 'green';
  }

}

<div class="div" [ngStyle]="{'background-color':successColor()}"> you are so sucessfull!</div>
