import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Databinding_Assignment';
  Username='';

  Reset(){
    this.Username="";
  }
}
<div>
<label>Username:</label>
<input type="text" class="form-control" [(ngModel)]="this.Username"/>
<p> {{this.Username}} </p>
<button (click)="Reset()" [disabled]="!this.Username" >Reset User</button>
</div>
