import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Directives_Assignment';
  isHide = true;
  count=0;
  logclick:number[]=[];

  toggle(){
    this.isHide=!this.isHide;
    this.count+=1;
    this.logclick.push(this.count);
  }
}
